<script>
import { mapState } from 'vuex';
import authPopover from './AuthPopover'
import appStat from './AppStat'
export default {
	name: 'headerTop',
	components: {
		authPopover,
		appStat
	},
	data() {
		return {
			browser: 'chrome'
		}
	},
	computed: {
		...mapState('user', ['auth', 'role']),
		browserIcon() {
			return `/${this.browser}.svg`;
		},
	},
	methods: {
		detectBrowser() {
			if (process.client) {
				var ua = window.navigator.userAgent;
				if (ua.search(/YaBrowser/) > 0)  this.browser = 'yandex';
				if (ua.search(/Opera/) > 0)  this.browser = 'opera';
				if (ua.search(/Google Inc/) > 0)  this.browser = 'chrome';
			}
		},
	},
	mounted() {
		this.detectBrowser();
	}
}
</script>

